import logo from './ibm-logo.svg';
import './App.css';
import { useState } from 'react';
import axios from 'axios';

function App() {
  const[firstName, setFirstName] = useState("");
  const[lastName, setLastName] = useState("");
  const[hireDate, setHireDate] = useState(Date);
  const[role, setRole] = useState("");


  const addEmployee = () =>{
    alert(firstName + lastName + hireDate + role);
    axios.post('/api/employees',
    {firstName: firstName, lastName:lastName, hireDate:hireDate,role:role})
          .then(() => {
            alert('User added');
          }).catch(() => {
            alert('it failed');
          });
  };
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>
      <div className="Inputs">
        <div className="Employee">
          <h3> IBM Employee Records</h3>
        </div>
        <div className="Records">
        <h5> Create Employee Record </h5>
        <label> FirstName 
          <input  type="text" 
                  placeholder="firstName" 
                  onChange={ (event) =>{
                    setFirstName(event.target.value);
                  }}/>
        </label>
        <label> LastName 
          <input  type="text" 
                  placeholder="lastName" 
                  onChange={ (event) =>{
                    setLastName(event.target.value);
                  }}/>
        </label>
        <label> Hire Date  
          <input  type="date" 
                  placeholder="Hire Date" 
                  onChange={ (event) =>{
                    setHireDate(event.target.value);
                  }}/>
        </label>
        <label> User Role 
          <input  type="text" 
                  placeholder="Role" 
                  onChange={ (event) =>{
                    setRole(event.target.value);
                  }}/>
        </label>
        <button onClick ={addEmployee}> Add Employee</button>
        </div>
      </div>
    </div>
  );
}

export default App;
